import React, { useState } from 'react';
import Clubs from './components/Clubs';
import Courses from './components/Courses';
import Testimonies from './components/Testimonies';
import './App.css';

export default function App() {
  const [currentPage, setCurrentPage] = useState('clubs');

  return (
    <div className="App">
      <header className="App-header">
        <h1>Welcome to HSE CS Promotion</h1>
      </header>
      <div className="app-container">
        <div className="nav-bar">
          <button onClick={() => setCurrentPage('clubs')}>Clubs</button>
          <button onClick={() => setCurrentPage('courses')}>Courses</button>
          <button onClick={() => setCurrentPage('testimonies')}>Testimonies</button>
        </div>
        <div className="content-area">
          {currentPage === 'clubs' && <Clubs />}
          {currentPage === 'courses' && <Courses />}
          {currentPage === 'testimonies' && <Testimonies />}
        </div>
      </div>
    </div>
  );
}
