import Club from '../models/Club';
import Course from '../models/Course';
import Testimony from '../models/Testimony';

export const clubs: Club[] = [
  new Club(1, 'Chess Club', 'Sharpen your strategy skills', 'We meet weekly to improve...'),
  new Club(2, 'Mathletes', 'Competitive mathematics team', 'Join our competitions...')
];

export function addClub(name: string, description: string, longDesc?: string) {
  const newId = clubs.length + 1;
  clubs.push(new Club(newId, name, description, longDesc));
}

export const courses: Course[] = [
  new Course(1, 'Intro to Programming', 'Learn the basics', 'In-depth coverage of variables...'),
  new Course(2, 'Data Structures', 'In-depth data handling', 'Covers arrays, trees, etc.')
];

export function addCourse(title: string, desc: string, longDesc?: string) {
  const newId = courses.length + 1;
  courses.push(new Course(newId, title, desc, longDesc));
}

export const testimonies: Testimony[] = [
  new Testimony('John Doe', 'Fantastic club!', 1, undefined),
  new Testimony('Jane Smith', 'Loved the course!', undefined, 2)
];

export function addTestimony(author: string, content: string) {
  testimonies.push(new Testimony(author, content));
}
