import Club from '../models/Club';
import Course from '../models/Course';
import Testimony from '../models/Testimony';

export const clubs: Club[] = [
  new Club(1, 'Chess Club', 'Sharpen your strategy skills', 'We meet weekly to improve...'),
  new Club(2, 'Mathletes', 'Competitive mathematics team', 'Join our competitions...'),
  new Club(3, 'Robotics Club', 'Innovate and build robots', 'Join us to design, build, and program robots for various competitions and projects. We provide resources and mentorship to help you bring your ideas to life.'),
  new Club(4, 'Art Society', 'Express your creativity', 'A space for artists of all levels to collaborate, create, and showcase artwork. We host workshops, exhibitions, and provide materials for diverse art forms.')
];

export function addClub(name: string, description: string, longDesc?: string) {
  const newId = clubs.length + 1;
  clubs.push(new Club(newId, name, description, longDesc));
}

export const courses: Course[] = [
  new Course(1, 'Intro to Programming', 'Learn the basics', 'In-depth coverage of variables...'),
  new Course(2, 'Data Structures', 'In-depth data handling', 'Covers arrays, trees, etc.'),
  new Course(3, 'Advanced Algorithms', 'Deep dive into algorithm design', 'Explore complex algorithms, optimization techniques, and their applications in solving real-world problems. Topics include graph algorithms, dynamic programming, and more.'),
  new Course(4, 'Web Development', 'Build modern web applications', 'Comprehensive coverage of front-end and back-end technologies. Learn HTML, CSS, JavaScript, frameworks like React and Node.js, and database management.')
];

export function addCourse(title: string, desc: string, longDesc?: string) {
  const newId = courses.length + 1;
  courses.push(new Course(newId, title, desc, longDesc));
}

export const testimonies: Testimony[] = [
  new Testimony('John Doe', 'Fantastic club!'),
  new Testimony('Jane Smith', 'Loved the course!'),
  new Testimony('Alice Johnson', 'The Robotics Club transformed my engineering skills!'),
  new Testimony('Bob Lee', 'Advanced Algorithms course was challenging but incredibly rewarding.'),
  new Testimony('Catherine Green', 'Art Society provided the perfect environment to grow my artistic talents.'),
  new Testimony('David Kim', 'Web Development course equipped me with the skills to build real-world applications.')
];

export function addTestimony(testimony: Testimony, clubId?: number, courseId?: number) {
  if (clubId) {
    testimony.clubId = clubId;
  }
  if (courseId) {
    testimony.courseId = courseId;
  }
  testimonies.push(testimony);
}
