import React, { useState } from 'react';
import Clubs from './components/Clubs';
import Courses from './components/Courses';
import Details from './components/Details';
import './App.css';

export default function App() {
  const [currentPage, setCurrentPage] = useState<'courses' | 'clubs' | 'details'>('courses');
  const [detailType, setDetailType] = useState<'club' | 'course'>('club');
  const [selectedId, setSelectedId] = useState<string | null>(null);

  function onSelectClub(id: string) {
    setDetailType('club');
    setSelectedId(id);
    setCurrentPage('details');
  }

  function onSelectCourse(id: string) {
    setDetailType('course');
    setSelectedId(id);
    setCurrentPage('details');
  }

  return (
    <div className="App">
      <header className="App-header">
        <h1>Welcome to HSE CS Promotion</h1>
      </header>
      <div className="app-container">
        <div className="nav-bar">
          <button onClick={() => setCurrentPage('courses')}>Courses</button>
          <button onClick={() => setCurrentPage('clubs')}>Clubs</button>
        </div>
        <div className="content-area">
          {currentPage === 'clubs' && <Clubs onSelectClub={onSelectClub} />}
          {currentPage === 'courses' && <Courses onSelectCourse={onSelectCourse} />}
          {currentPage === 'details' && selectedId && (
            <Details
              itemType={detailType}
              itemId={selectedId}
              onBack={() => setCurrentPage(detailType === 'club' ? 'clubs' : 'courses')}
            />
          )}
        </div>
      </div>
    </div>
  );
}
